using System.ComponentModel.Composition;
using XrmToolBox.Extensibility;
using XrmToolBox.Extensibility.Interfaces;

namespace Thrives.XrmToolBox.EntityUsage
{

    [Export(typeof(IXrmToolBoxPlugin)),
        ExportMetadata("Name", "Entity Usage"),
        ExportMetadata("Description", "Display's information regarding entity usage"),
        // Please specify the base64 content of a 32x32 pixels image
        ExportMetadata("SmallImageBase64", ""),
        // Please specify the base64 content of a 80x80 pixels image
        ExportMetadata("BigImageBase64", ""),
        ExportMetadata("BackgroundColor", "transparent"),
        ExportMetadata("PrimaryFontColor", "#EA8651"),
        ExportMetadata("SecondaryFontColor", "#5B6F80")]
    public class MyPlugin : PluginBase
    {
        public override IXrmToolBoxPluginControl GetControl()
        {
            return new MyPluginControl();
        }
    }
}